import { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";

export function Routing() {
    const GET = () => {
        //must use hook to render upon change
      const [product, setProduct] = useState([]);
      //use affect always executes when the function is called so content can be changed
      useEffect(() => {
        getAllProducts();
      }, []);
    
      function getAllProducts() {
        fetch("http://127.0.0.1:4000/api/get")
          .then((response) => response.json())
          .then((data) => {
            console.log("Show Catalog of Products :");
            console.log(data);
            setProduct(data);
          });
      }
    
      const showAllItems = product.map((el) => (
        <div key={el.id}>
          <img src={el.image} width={30} alt="images" /> <br />
          Id: {el.id} <br />
          Title: {el.title} <br />
          Category: {el.category} <br />
          Price: {el.price} <br />
          Rating :{el.rating} <br />
        </div>
      ));
    
      return (
        <div>
          {/* ------------- Method GET all products -----------------*/}
          <h1>Catalog of Products</h1>
          <div>
            <h3>Show all available Products.</h3>
            <div>Products {showAllItems}</div>
          </div>
        </div>
      );
    };
    
  return (
    <Routing>
      <div>
        {/* Routes: map a path to a module */}
        <Routes>
          <Route path="/get" element={<GET />} />
          <Route path="/getid" element={<GETID />} />
        </Routes>

        {/* Navigation Bar link to the module */}
        <nav>
          <ul>
            <li>
              <Link to="/get">Get all products</Link>
            </li>
            <li>
              <Link to="/getid">Get by Id</Link>
            </li>
          </ul>
        </nav>
      </div>
    </Routing>
  );
}

export default Routing;
