  const showOneItem = oneProduct.map((el) => (
    <div key={el.id}>
    <img src={el.image} width={30} alt="images" /> <br />
    Title: {el.title} <br />
    Category: {el.category} <br />
    Price: {el.price} <br />
    Rating: {el.rating} <br />
    </div>
    ));

  console.log(id);
  if (id >= 1 && id <= 20) {
  fetch("http://127.0.0.1:4000/api/getFromId/" + id)
