import { useState } from "react";

function Put() {
  const [editedProduct, setEditedProduct] = useState({
    id: 0,
    title: "",
    price: 0.0,
    description: "",
    category: "",
    image: "http://127.0.0.1:4000/images/",
    rating: 0.0,
  });

  function handleChange(evt) {
    const { name, value } = evt.target;
    setEditedProduct((prevProduct) => ({
      ...prevProduct,
      [name]: name === "rating" ? parseFloat(value) : value,
    }));
  }

  function handleIdChange(evt) {
    const productId = parseInt(evt.target.value, 10);
    setEditedProduct((prevProduct) => ({ ...prevProduct, id: productId }));
  }

  function handleOnSubmit(e) {
    e.preventDefault();
    const productId = editedProduct.id;

    // console.log(productId);

    fetch(`http://127.0.0.1:8081/updateProduct/` + productId, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(editedProduct),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Edit product completed");
        console.log(data);
        if (data) {
          const value = Object.values(data);
          alert(value);
        }
      });
  }

  return (
    <div>
      <div className="editProductContent">
        <h3 className="Heading">Edit Product:</h3>
        <form action="">
          <label>
            Product ID:
            <input
              type="number"
              placeholder="Enter Product ID"
              name="id"
              value={editedProduct.id}
              onChange={handleIdChange}
            />
          </label>
          <br />
          <label>
            Title:
            <input
              type="text"
              placeholder="Enter Title"
              name="title"
              value={editedProduct.title}
              onChange={handleChange}
            />
          </label>
          <br />
          <label>
            Price:
            <input
              type="number"
              placeholder="Enter Price"
              name="price"
              value={editedProduct.price}
              onChange={handleChange}
            />
          </label>
          <br />
          <label>
            Description:
            <input
              type="text"
              placeholder="Enter Description"
              name="description"
              value={editedProduct.description}
              onChange={handleChange}
            />
          </label>
          <br />
          <label>
            Category:
            <input
              type="text"
              placeholder="Enter Category"
              name="category"
              value={editedProduct.category}
              onChange={handleChange}
            />
          </label>
          <br />
          <label>
            Image:
            <input
              type="text"
              placeholder="Enter Image URL"
              name="image"
              value={editedProduct.image}
              onChange={handleChange}
            />
          </label>
          <br />
          <label>
            Rate:
            <input
              type="number"
              placeholder="Enter Rate"
              name="rating"
              value={editedProduct.rating}
              onChange={handleChange}
            />
          </label>
          <br />
          <button type="submit" onClick={handleOnSubmit}>Update</button>
        </form>
      </div>
    </div>
  );
}

export default Put;
