const GET = () => {
    //must use hook to render upon change
  const [product, setProduct] = useState([]);
  //use affect always executes when the function is called so content can be changed
  useEffect(() => {
    getAllProducts();
  }, []);

  function getAllProducts() {
    fetch("http://127.0.0.1:4000/api/get")
      .then((response) => response.json())
      .then((data) => {
        console.log("Show Catalog of Products :");
        console.log(data);
        setProduct(data);
      });
  }

  const showAllItems = product.map((el) => (
    <div key={el.id}>
      <img src={el.image} width={30} alt="images" /> <br />
      Id: {el.id} <br />
      Title: {el.title} <br />
      Category: {el.category} <br />
      Price: {el.price} <br />
      Rating :{el.rating} <br />
    </div>
  ));

  return (
    <div>
      {/* ------------- Method GET all products -----------------*/}
      <h1>Catalog of Products</h1>
      <div>
        <h3>Show all available Products.</h3>
        <div>Products {showAllItems}</div>
      </div>
    </div>
  );
};
