import { useState, useEffect } from "react";
import "./App.css"; // Import your CSS file

const Product = ({ product }) => (
  <div key={product.id}>
    <img src={product.image} width={30} alt="images" /> <br />
    Id: {product.id} <br />
    Title: {product.title} <br />
    Category: {product.category} <br />
    Price: {product.price} <br />
    Rating: {product.rating.count}, {product.rating.rate} <br />
  </div>
);

function Delete() {
  const [product, setProduct] = useState([]);
  const [index, setIndex] = useState(0);

  useEffect(() => {
    getAllProducts();
  }, []);

  function getAllProducts() {
    fetch("http://127.0.0.1:8081/fakestore")
      .then((response) => response.json())
      .then((data) => {
        console.log("Show Catalog of Products :");
        console.log(data);
        setProduct(data);
      });
  }

  function getOneByOneProductNext() {
    setIndex((prevIndex) => (prevIndex + 1) % product.length);
  }

  function getOneByOneProductPrev() {
    setIndex((prevIndex) => (prevIndex - 1 + product.length) % product.length);
  }

  function deleteOneProduct(deleteid) {
    console.log("Product to delete :", deleteid);
    fetch("http://localhost:8081/deleteProduct", {
      method: "DELETE",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ id: deleteid }),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Delete a product completed : ", deleteid);
        console.log(data);
        if (data) {
          const key = Object.keys(data);
          const value = Object.values(data);
          alert(key + value);
        }
      });

    getAllProducts();
  }

  return (
    <div>
      <h3 className="Heading">Delete one product:</h3>
      <button onClick={getOneByOneProductPrev}>Prev</button>
      <button onClick={getOneByOneProductNext}>Next</button>
      <button onClick={() => deleteOneProduct(product[index].id)}>
        Delete
      </button>
      <Product product={product[index]} />
    </div>
  );
}

export default Delete;
