import React from "react";

const AboutUs = () => {
    const containerStyle = {
      display: 'block',
      marginLeft: 'auto',
      marginRight: 'auto',
      backgroundColor: 'pink',
      width: '40%',
      padding: '20px',
      borderRadius: '100%',
      marginTop: '40px',
    };
  
    const imageStyle = {
      display: 'block',
      marginLeft: 'auto',
      marginRight: 'auto',
      width: '100%',
    };
  
    const headingStyle = {
      textAlign: 'center',
      fontSize: '10vw',
      fontFamily: 'Lucida Handwriting, cursive',
    };
  
    const paragraphStyle = {
      margin: '30px 20% 30px 20%',
    };
  
    return (
      <div>
        <div style={containerStyle}>
          <img style={imageStyle} width="100%" src={process.env.PUBLIC_URL + '/characters/messageBearNoBG.png'} alt="Amero Bakery" />
        </div>
        <h1 style={headingStyle}>About Us</h1>
        <p style={paragraphStyle}>
          {/* ... Your content here ... */}
        </p>
      </div>
    );
  };
  
  export default AboutUs;


